import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';
import Row from './components/Row';

const url = 'http://localhost:3001/'

function App() {

  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    axios.get(url)
    .then(response => {
      console.log('Fetched tasks:', response.data);
      setTasks(response.data) 
    }).catch(error => {
      console.error('Error fetching tasks:', error);
      alert(error.response?.data?.error ? error.response.data.error : error.message)
    })
  }, [])

  // const addTask = () => {
  //   // const headers = {headers: {Authorization:user.token}}
  //   axios.post(url + 'create', {
  //     description: task
  //   })
  //   .then(response => {
  //     console.log('Task added: ' ,response.data)
  //     setTasks([...tasks,{id: response.data.id,description: task}])
  //     setTask('')
  //   }).catch(error => {
  //       alert (error.response?.data?.error ? error.response.data.error : error)
  //   })
  // }
  const addTask = () => {
    axios.post(url + 'create', { description: task })
      .then(response => {
        const newTaskId = response.data.id?.id || response.data.id;

        // if (isNaN(newTaskId)) {
        //   console.error('Invalid task ID from response:', response.data.id);
        //   return alert('Received invalid task ID. Task not added.');
        // }

        const newTask = {
          id: newTaskId,
          description: task
        };
        setTasks([...tasks, newTask]);
        setTask('');
      })
      .catch(error => {
        // console.error('Error adding task:', error);
        alert(error.response?.data?.error ? error.response.data.error : error.message);
      });
  };



  // const deleteTask = (id) => {
  //   // const headers = {headers: {Authorization:user.token}}
  //   axios.delete(url + 'delete/' + id) 
  //   .then(response => {
  //     const withoutRemoved = tasks.filter((item) => item.id !== id )
  //     setTasks(withoutRemoved)
  //   }).catch(error => {
  //       alert (error.response?.data?.error ? error.response.data.error : error)
  //   })
  // }


  const deleteTask = (id) => {
    if (isNaN(id)) {
      console.error('Invalid ID:', id);
      return alert('Invalid task ID. Cannot delete.');
    }

    axios.delete(url + 'delete/' + id)
      .then(response => {
        const withoutRemoved = tasks.filter(item => item.id !== id);
        setTasks(withoutRemoved);
      })
      .catch(error => {
        console.error('Error deleting task:', error);
        alert(error.response?.data?.error ? error.response.data.error : error.message);
      });
  };

  return (
    <div id="container">
      <h3>Todos</h3>
      <form>
        <input type="Add new task" 
         value={task}
         onChange={e => setTask(e.target.value)}
         onKeyDown={e => {
           if (e.key === 'Enter') {
             e.preventDefault()
             addTask()
           }
         }}
          />
      </form>
      {/* <ul>
        {
          tasks.map(item => (
            <Row key={item.id} item={item} deleteTask={deleteTask} />
          ))
        }
      </ul> */}

<ul>
  {tasks.map(item => (
    <Row key={item.id} item={item} deleteTask={deleteTask} />
  ))}
</ul>

    </div>
  );
}

export default App;


